insert  into `t_video`(`id`,`systematics`,`description`,`manager`,`period`,`step_count`,`video_url`,`create_time`,`create_user`,`update_time`,`update_user`,`school_id`,`sort`) values (1,'土木工程','本实验以滑坡地质灾害及其综合防治为虚拟仿真对象，通过虚拟仿真技术重现滑坡的发生过程及其细部特征，供学生学习滑坡 特征的认识、识别及工程治理。实验内容主要分为滑坡的基本知识点、滑坡的虚拟仿真及实验结果测验三部分。其中对于滑坡 的机理和滑动过程进行了细致的仿真，滑坡面、滑坡台阶、滑坡裂缝（张拉、剪切）、醉林等滑坡特征点均具有较高仿真程度， 能让学生直观地观察滑坡发生的整个过程。','郑先昌','2','66','http://47.106.255.216/landslide/mp4/滑坡项目简介视频.mp4','2020-10-02 17:46:16','zhuang','2020-10-02 17:46:24','zhuang',1,1),(2,'土木工程','本实验以滑坡地质灾害及其综合防治为虚拟仿真对象，通过虚拟仿真技术重现滑坡的发生过程及其细部特征，供学生学习滑坡 特征的认识、识别及工程治理。实验内容主要分为滑坡的基本知识点、滑坡的虚拟仿真及实验结果测验三部分。其中对于滑坡 的机理和滑动过程进行了细致的仿真，滑坡面、滑坡台阶、滑坡裂缝（张拉、剪切）、醉林等滑坡特征点均具有较高仿真程度， 能让学生直观地观察滑坡发生的整个过程。','郑先昌','2','66','http://47.106.255.216/landslide/mp4/滑坡项目简介视频.mp4','2020-10-02 17:46:16','zhuang','2020-10-02 17:46:24','zhuang',1,2);

